import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angular-frontend';
}
<nav class="navbar navbar-light bg-light">
    <form class="container-fluid justify-content-start">
        <button routerLink="employees" class="btn btn-outline-success me-2" type="button">Employee List</button>
        <button routerLink="create-employee" class="btn btn-outline-info me-2" type="button">Create Employee</button>
    </form>
</nav>
<h1 class="text-center"> Employee Management System</h1>
<div class="container">
<router-outlet></router-outlet>
</div>
